}


@Getter
@Setter
@Builder(toBuilder = true)
@NoArgsConstructor
@AllArgsConstructor
@ToString(onlyExplicitlyIncluded = true)
@EqualsAndHashCode(onlyExplicitlyIncluded = true)
public class UserDTO implements Serializable {
    private static final long serialVersionUID = -8615427425887155741L;

    @ToString.Include
    private String uid;
    @ToString.Include
    private String firstName;
    @ToString.Include
    private String lastName;
    @ToString.Include
    @EqualsAndHashCode.Include
    private String email;

    private AuditDTO audit;
}


class UserDTOTest {

    @Test
    void userDTO() {
        assertDto(AssertDtoContext.<UserDTO>builder()
                                  .objectClass(UserDTO.class)
                                  .cloneFunction(i -> i.toBuilder().build())
                                  .noArgConstructor(UserDTO::new)
                                  .fullArgConstructor(this::buildUserDTO)
                                  .fullArgConstructorRefPath("api/domain/user/dto/userDTO/model.json")
                                  .getterRefPath("api/domain/user/dto/userDTO/getter.json")
                                  .toStringRefPath("api/domain/user/dto/userDTO/toString.txt")
                                  .noEqualsFunction(this::notEquals)
                                  .checkSetters(true)
                                  .build());
    }

    private void notEquals(final UserDTO instance) {
        assertThat(instance).isNotEqualTo(null);
        assertThat(instance).isNotEqualTo(instance.toBuilder());
        //
        assertThat(instance).isNotEqualTo(instance.toBuilder().email(null).build());
        assertThat(instance.toBuilder().email(null).build()).isNotEqualTo(instance);
        assertThat(instance).isNotEqualTo(instance.toBuilder().email(UnitTestData.OTHER).build());
        assertThat(instance.toBuilder().email(UnitTestData.OTHER).build()).isNotEqualTo(instance);
        assertThat(instance.hashCode()).isNotEqualTo(instance.toBuilder().email(null).build().hashCode());
        assertThat(instance.hashCode()).isNotEqualTo(instance.toBuilder()
                                                             .email(UnitTestData.OTHER)
                                                             .build()
                                                             .hashCode());
    }

    private UserDTO buildUserDTO() {
        return UserDTO.builder()
                      .uid(UnitTestData.UID)
                      .firstName(UnitTestData.FIRSTNAME)
                      .lastName(UnitTestData.LASTNAME)
                      .email(UnitTestData.EMAIL)
                      .build()
                      .toBuilder()
                      .build();
    }
}


@ToString
@Setter
@Getter
@Builder(toBuilder = true)
@AllArgsConstructor
@NoArgsConstructor
public class UserDTOSearchRequestDTO implements SearchRequest , Serializable {
    private static final long         serialVersionUID = -6646958578723667636L;
    // SearchRequest
    private Integer                   page;
    private Integer                   pageSize;
    private String                    sortFields;
    private SortOrder                 sortOrder;
    @Singular("createdBy")
    private Collection<String>        createdBy;
    @Singular("createdDate")
    private Collection<LocalDateTime> createdDate;
    @Singular("lastModifiedBy")
    private Collection<String>        lastModifiedBy;
    @Singular("lastModifiedDate")
    private Collection<LocalDateTime> lastModifiedDate;

    // UserDTOSearchRequestDTO
    @Singular("uid")
    private Collection<String> uid;
    @Singular("firstName")
    private Collection<String> firstName;
    @Singular("lastName")
    private Collection<String> lastName;
    @Singular("email")
    private Collection<String> email;
}

class UserDTOSearchRequestDTOTest { @Test void userDTOSearchRequestDTO() { assertDto(AssertDtoContext.<UserDTOSearchRequestDTO>builder() .objectClass(UserDTOSearchRequestDTO.class) .cloneFunction(i -> i.toBuilder().build()) .noArgConstructor(UserDTOSearchRequestDTO::new) .fullArgConstructor(this::buildUserDTOSearchRequestDTO) .fullArgConstructorRefPath("api/domain/user/dto/userDTOSearchRequestDTO/model.json") .getterRefPath("api/domain/user/dto/userDTOSearchRequestDTO/getter.json") .toStringRefPath("api/domain/user/dto/userDTOSearchRequestDTO/toString.txt") .checkEquals(false) .checkSetters(true) .build()); }
private UserDTOSearchRequestDTO buildUserDTOSearchRequestDTO() { return UserDTOSearchRequestDTO.builder() .page(0) .pageSize(10) .sortFields("name") .sortOrder(SortOrder.ASC) .createdBy("system") .clearCreatedBy() .createdBy(List.of("system")) .createdDate(UnitTestData.DATE_TIME.minusDays(5)) .clearCreatedDate() .createdDate(List.of(UnitTestData.DATE_TIME.minusDays(5))) .lastModifiedBy("system2") .clearLastModifiedBy() .lastModifiedBy(List.of("system2")) .lastModifiedDate(UnitTestData.DATE_TIME) .clearLastModifiedDate() .lastModifiedDate(List.of(UnitTestData.DATE_TIME)) //------------------------------------------------------------------------------ .uid(UnitTestData.UID) .clearUid() .uid(List.of(UnitTestData.UID))
.firstName(UnitTestData.FIRSTNAME) .clearFirstName() .firstName(List.of(UnitTestData.FIRSTNAME))
.lastName(UnitTestData.LASTNAME) .clearLastName() .lastName(List.of(UnitTestData.LASTNAME))
.email(UnitTestData.EMAIL) .clearEmail() .email(List.of(UnitTestData.EMAIL))
//------------------------------------------------------------------------------ .build() .toBuilder() .build(); }


@UtilityClass
public class UserFilters {

    public static final String UID        = "uid";
    public static final String FIRST_NAME = "firstName";
    public static final String LAST_NAME  = "lastName";
    public static final String EMAIL      = "email";


    public static final List<QueryFilterDTO<?>> FILTERS = buildFilters(
            QueryFilterDTO.<String>builder()
                          .field(UID)
                          .matchType(MatchType.IN)
                          .build(),
            QueryFilterDTO.<String>builder()
                          .field(FIRST_NAME)
                          .matchType(MatchType.CONTAINS)
                          .build(),
            QueryFilterDTO.<String>builder()
                          .field(LAST_NAME)
                          .matchType(MatchType.CONTAINS)
                          .build(),
            QueryFilterDTO.<String>builder()
                          .field(EMAIL)
                          .matchType(MatchType.IN)
                          .build()
                                                                      );
}


